mapper.select:
SELECT 

FROM department a

WHERE a.id = #{id}

SELECT 

FROM department a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.flag = #{flag}


		AND a.hotel_id = #{hotelId}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM department a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE department SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
