mapper.select:
SELECT 

FROM hotel_channel a

WHERE a.id = #{id}

SELECT
FROM hotel_channel a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.remarks LIKE '%'||#{remarks}||'%' '%'+#{remarks}+'%' concat('%',#{remarks},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.hotel_id = #{hotelId}
AND a.contacts LIKE '%'||#{contacts}||'%' '%'+#{contacts}+'%' concat('%',#{contacts},'%')
AND a.contacts_phone LIKE '%'||#{contactsPhone}||'%' '%'+#{contactsPhone}+'%' concat('%',#{contactsPhone},'%')
AND a.contract_period BETWEEN #{beginContractPeriod} AND #{endContractPeriod}
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM hotel_channel a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE hotel_channel SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
