mapper.select:
SELECT 

FROM member a

WHERE a.id = #{id}

SELECT
FROM member a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.hotel_id = #{hotel.id}
AND a.id_num LIKE '%'||#{idNum}||'%' '%'+#{idNum}+'%' concat('%',#{idNum},'%')
AND a.birthday = #{birthday}
AND a.phone LIKE '%'||#{phone}||'%' '%'+#{phone}+'%' concat('%',#{phone},'%')
AND a.emergency_contact LIKE '%'||#{emergencyContact}||'%' '%'+#{emergencyContact}+'%' concat('%',#{emergencyContact},'%')
AND a.emergency_contact_phone LIKE '%'||#{emergencyContactPhone}||'%' '%'+#{emergencyContactPhone}+'%' concat('%',#{emergencyContactPhone},'%')
AND a.member_num LIKE '%'||#{memberNum}||'%' '%'+#{memberNum}+'%' concat('%',#{memberNum},'%')
AND a.menber_level_id = #{memberLevel.id}
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM member a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE member SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
