mapper.select:
SELECT 

FROM member a

WHERE a.id = #{id}

SELECT
FROM member a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.nick_name LIKE '%'||#{nickName}||'%' '%'+#{nickName}+'%' concat('%',#{nickName},'%')
AND a.hotel_id = #{hotelId}
AND a.id_num = #{idNum}
AND a.birthday = #{birthday}
AND a.phone LIKE '%'||#{phone}||'%' '%'+#{phone}+'%' concat('%',#{phone},'%')
AND a.emergency_contact LIKE '%'||#{emergencyContact}||'%' '%'+#{emergencyContact}+'%' concat('%',#{emergencyContact},'%')
AND a.emergency_contact_phone LIKE '%'||#{emergency_contactPhone}||'%' '%'+#{emergency_contactPhone}+'%' concat('%',#{emergency_contactPhone},'%')
AND a.member_num = #{memberNum}
AND a.menber_level_id = #{menberLevelId}
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM member a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE member SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
