mapper.select:
SELECT

FROM book_order a

WHERE a.id = #{id}

SELECT

FROM book_order a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.remarks LIKE
        '%'||#{remarks}||'%'
        '%'+#{remarks}+'%'
        concat('%',#{remarks},'%')


        AND a.order_num LIKE
        '%'||#{orderNum}||'%'
        '%'+#{orderNum}+'%'
        concat('%',#{orderNum},'%')


        AND a.channel_id = #{channelId}


        AND a.hotel_id = #{hotelId}


        AND a.roomtype_id = #{roomTypeId}


        AND a.lease_mode = #{leaseMode}


        AND a.rent_months = #{rentMonths}


        AND a.book_time LIKE
        '%'||#{bookTime}||'%'
        '%'+#{bookTime}+'%'
        concat('%',#{bookTime},'%')


        AND a.checkin_date BETWEEN #{beginCheckInDate} AND #{endCheckInDate}


        AND a.contacts LIKE
        '%'||#{contacts}||'%'
        '%'+#{contacts}+'%'
        concat('%',#{contacts},'%')


        AND a.contacts_phone LIKE
        '%'||#{contactsPhone}||'%'
        '%'+#{contactsPhone}+'%'
        concat('%',#{contactsPhone},'%')


        AND a.book_room_id = #{bookRoomId}


        AND a.status = #{status}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM book_order a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE book_order SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
