mapper.select:
SELECT 

FROM resident a

WHERE a.id = #{id}

SELECT
FROM resident a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.id_num LIKE '%'||#{idNum}||'%' '%'+#{idNum}+'%' concat('%',#{idNum},'%')
AND a.phone LIKE '%'||#{phone}||'%' '%'+#{phone}+'%' concat('%',#{phone},'%')
AND a.gender = #{gender}
AND a.resident_type = #{residentType}
AND a.hotel_id = #{hotel}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM resident a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE resident SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
