mapper.select:
SELECT

FROM room a

WHERE a.id = #{id}

SELECT

FROM room a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.remarks LIKE
			'%'||#{remarks}||'%'
			'%'+#{remarks}+'%'
			concat('%',#{remarks},'%')


		AND a.room_num = #{roomNum}


		AND a.topic_name LIKE
			'%'||#{topicName}||'%'
			'%'+#{topicName}+'%'
			concat('%',#{topicName},'%')


		AND a.hotel_id = #{hotel.id}


		AND a.floor_num = #{floorNum}


		AND a.layout LIKE
			'%'||#{layout}||'%'
			'%'+#{layout}+'%'
			concat('%',#{layout},'%')


		AND a.roomtype_id = #{roomType.id}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM room a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE room SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
