mapper.select:
SELECT 

FROM staff a

WHERE a.id = #{id}

SELECT
FROM staff a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.remarks LIKE '%'||#{remarks}||'%' '%'+#{remarks}+'%' concat('%',#{remarks},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.phone LIKE '%'||#{phone}||'%' '%'+#{phone}+'%' concat('%',#{phone},'%')
AND a.hotel_id = #{hotelId}
AND a.id_num LIKE '%'||#{idNum}||'%' '%'+#{idNum}+'%' concat('%',#{idNum},'%')
AND a.birthday = #{birthday}
AND a.gender = #{gender}
AND a.home_address LIKE '%'||#{homeAddress}||'%' '%'+#{homeAddress}+'%' concat('%',#{homeAddress},'%')
AND a.department_id = #{departmentId}
AND a.post_id = #{postId}
AND a.contract_period BETWEEN #{beginContractPeriod} AND #{endContractPeriod}
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM staff a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE staff SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
